// UPLOAD ATTACHMENTS
var images = [];

function image_select() {
    var image = document.getElementById('image').files;
    for (i = 0; i < image.length; i++) {
        if (check_duplicate(image[i].name)) {
            images.push({
                "name": image[i].name,
                "url": URL.createObjectURL(image[i]),
                "file": image[i],
            })
        } else {
            alert(image[i].name + " is already added to the list.");
        }
    }
    document.getElementById('form').reset();
    document.getElementById('container').innerHTML = image_show();
}

function image_show() {
    var image = "";
    images.forEach((i) => {
        image += `<div class="image_container d-flex justify-content-center position-relative">
   	  	  	  	  <img src="` + i.url + `" alt="Image">
   	  	  	  	  <span class="position-absolute" onclick="delete_image(` + images.indexOf(i) + `)">&times;</span>
   	  	  	  </div>`;
    })
    return image;
}

function delete_image(e) {
    images.splice(e, 1);
    document.getElementById('container').innerHTML = image_show();
}

function check_duplicate(name) {
    var image = true;
    if (images.length > 0) {
        for (e = 0; e < images.length; e++) {
            if (images[e].name == name) {
                image = false;
                break;
            }
        }
    }
    return image;
}

var actimages = [];

function select_image() {
    var image_act = document.getElementById('act-image').files;
    for (i = 0; i < image_act.length; i++) {
        if (duplicate_check(image_act[i].name)) {
            actimages.push({
                "name": image_act[i].name,
                "url": URL.createObjectURL(image_act[i]),
                "file": image_act[i],
            })
        } else {
            alert(image_act[i].name + " is already added to the list.");
        }
    }
    document.getElementById('form').reset();
    document.getElementById('act-container').innerHTML = show_image();
}

function show_image() {
    var image_act = "";
    actimages.forEach((i) => {
        image_act += `<div class="act_image_container d-flex justify-content-center position-relative">
   	  	  	  	  <img src="` + i.url + `" alt="Image">
   	  	  	  	  <span class="position-absolute" onclick="image_delete(` + actimages.indexOf(i) + `)">&times;</span>
   	  	  	  </div>`;
    })
    return image_act;
}

function image_delete(e) {
    actimages.splice(e, 1);
    document.getElementById('act-container').innerHTML = show_image();
}

function duplicate_check(name) {
    var image_act = true;
    if (actimages.length > 0) {
        for (e = 0; e < actimages.length; e++) {
            if (actimages[e].name == name) {
                image_act = false;
                break;
            }
        }
    }
    return image_act;
}

